<template>
    <div>
        <h2>Login</h2>
        <form @submit.prevent="handleSubmit">
            <div class="form-group">
                <label for="identifier">identifier</label>
                <input type="text" v-model="identifier" name="identifier" class="form-control" :class="{ 'is-invalid': submitted && !identifier }" />
                <div v-show="submitted && !identifier" class="invalid-feedback">identifier is required</div>
            </div>
            <div class="form-group">
                <label htmlFor="password">Password</label>
                <input type="password" v-model="password" name="password" class="form-control" :class="{ 'is-invalid': submitted && !password }" />
                <div v-show="submitted && !password" class="invalid-feedback">Password is required</div>
            </div>
            <div class="form-group">
                <button class="btn btn-primary" :disabled="loggingIn">Login</button>
                <img v-show="loggingIn" src="data:image/gif;base64," />
                <router-link to="/register" class="btn btn-link">Register</router-link>
            </div>
        </form>
    </div>
</template>

<script>
export default {
    data () {
        return {
            identifier: '',
            password: '',
            submitted: false
        }
    },
    computed: {
        loggingIn () {
            return this.$store.state.authentication.status.loggingIn;
        },
    },

    created () {
        // reset login status
        this.$store.dispatch('authentication/logout');
    },

    methods: {
        handleSubmit:function (e) {
            this.submitted = true;
            const { identifier, password } = this;
            if (identifier && password) {
                this.$store.dispatch('authentication/login', { identifier, password });
            }
        }
    }
};
</script>